

const swiper = new Swiper('.swiper', {
  breakpoints: {
    0: {
      slidesPerView: 1,

    },

    1250: {
      slidesPerView: 1.95,
      centredSlides: true,
      slideToClickedSlide: true,
      navigation: {
        nextEl: '.custom__button-next',
        prevEl: '.custom__button-prev',
        clickable: true,
      },
    }
  }
})


let slideBtnOne = document.querySelector('.custom__button-prev');
let slideBtnTwo = document.querySelector('.custom__button-next');

function removeBlur() {
  let activeSlide = document.querySelector('.swiper-slide-active');
  let nextSlide = document.querySelector('.swiper-slide-next');

  if (this === slideBtnOne) {
    activeSlide.style.filter = 'none'; 
    nextSlide.style.filter = ''; 
  } else if (this === slideBtnTwo) {
    nextSlide.style.filter = 'none';
    activeSlide.style.filter = '';
  }
}

slideBtnOne.addEventListener('click', removeBlur);
slideBtnTwo.addEventListener('click', removeBlur);

let links = document.querySelectorAll('.nav__link');

if (links) {
  links.forEach(function(link) {
    link.addEventListener('click', function(e) {
      links.forEach(function(otherLink) {
        if (otherLink !== link) {
          otherLink.classList.remove('active');
        }
      });
      
      link.classList.toggle('active');
    });
  });
}

let burger = document.querySelector('.info__burger');

if (burger) {
  burger.addEventListener('click', function() {
    burger.classList.toggle('active')
  })
}